psql -h localhost -U postgres -d dbrief -c "
SELECT 
    COUNT(*) as total,
    COUNT(*) FILTER (WHERE ativo = true) as ativas,
    COUNT(*) FILTER (WHERE ativo = false) as inativas
FROM secretarias;
" 2>&1

