#!/bin/bash

# Script para verificar secretarias no banco de dados
# Mostra todas as secretarias (ativas e inativas) com detalhes

echo "🔍 Verificando secretarias no banco de dados..."
echo ""

# Verificar todas as secretarias
echo "📋 Todas as secretarias no banco:"
echo ""
psql -h localhost -U postgres -d dbrief -c "
SELECT 
    id,
    nome,
    cliente_id,
    ativo,
    created_at
FROM secretarias
ORDER BY nome, cliente_id;
" 2>&1

